mapper.insert:
INSERT INTO bbs (bbsid, ref, title, writer, password, userid, content, sec, category)
VALUES (
  #{bbsid}, #{id}, #{title}, #{writer}, #{password}, #{userid}, #{content}, #{sec}, #{category}
)

mapper.select:
SELECT * FROM bbs
WHERE bbsid=#{bbsid}
ORDER BY ref DESC, step ASC
LIMIT #{page}, #{recordsPerPage}
